import { Component, Directive } from '@angular/core';
import { NgForm } from '@angular/forms';
declare var $: any;
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'calory-tracker';
  filterName:string='';
  public show = false;
  public loop1=false;
  public loop2=true;
  s=0;
  items:any=[];
  newItem={
    name:"",
    caloriesCount:"",
    image:""
  };
  oldItem={
    name:"",
    caloriesCount:"",
    image:""
  };

  foodItems=[
      {
          image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6bzyNrFXOc0spgagtyX7Y6RVpsyZRjZEvxyW4yatugzT85n_iRhfcI3aY3kpvrpbn7-8&usqp=CAU",
          name:"Apple",
          caloriesCount:"81"
      }, 
      {
          image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ7oGPrgfY-YCoXVwbsjYkH0bkPPZoAOPhZ4aWM8pV_XFFAzxWZTPiND7o5l4vLZOPOhNk&usqp=CAU",
          name:"Banana",
          caloriesCount:"105"
      }, 
      {
          image:"data:image/jpeg;base64,",
          name:"Dates",
          caloriesCount:"228"
      },
      {
          image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRSQ5x9-S7Bve-plhHgoaQWk0SH0GsUb0mhKc_5hFMfDOI_VbzKnN3QTLPbqca8fiRGPig&usqp=CAU",
          name:"Grapes",
          caloriesCount:"114"
      }, 
      {
          image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQwS36oXKmVrU37CUg5HEj9RtvB2wZwdEyX2_O5uxbjOup_OXS3UyO8ppFR4g5f1MvPD_E&usqp=CAU",
          name:"Biscuits",
          caloriesCount:"103"
      },
      {
          image:"data:image/jpeg;base64,",
          name:"Strawberries",
          caloriesCount:"45"
      }
  ];

  generateForm(){
    this.show = ! this.show;
  }
  
  addFoodItem(form:NgForm){
    this.show = ! this.show;
    this.loop1=! this.loop1;
    this.loop2=! this.loop2;
    this.foodItems.splice(0,0,{
        image:this.newItem.image,
        name:this.newItem.name,
        caloriesCount:this.newItem.caloriesCount,
    });
    console.log(this.foodItems);
    form.reset();
  }

  addItems(oldItem:any){
      
      let sum=0;
      this.oldItem.name=oldItem.name;
      this.oldItem.caloriesCount=oldItem.caloriesCount;
      this.oldItem.image=oldItem.image;
      this.items.push(oldItem);
      for(let i of this.items){
        let count=$('#'+oldItem.name).val();
        if(count.length==0){
          sum=sum+parseInt(i.caloriesCount,10);
          i.caloriesCount=i.caloriesCount
        }
        else{
          sum=sum+(parseInt(i.caloriesCount,10)*parseInt(count));
          i.caloriesCount=i.caloriesCount
        }
        if(i.name==='Grapes'){
          i.caloriesCount="(1 cup) "+i.caloriesCount;
        }
        $('#'+oldItem.name).val("");
      }
      console.log(this.items);
      this.s=sum;
  }
  
}

